sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	var start = g_form.getValue('start_date');
	var ga=new GlideAjax('DurationAjax');
	ga.addParam('sysparm_name','getDateDifference');
	ga.addParam('sysparm_start',start);
	ga.addParam('sysparm_end',newValue);
	ga.getXML(getDuration);

	function getDuration(response)
	{
		var ans=response.responseXML.documentElement.getAttribute("answer");
		var days=(ans) / (1000*60*60*24);     //covert days from milliseconds
		var day1=days+1;
		g_form.setValue('duration',day1);
	}

   //Type appropriate comment here, and begin script below

}
